import { FASTElement, type ViewTemplate, type ElementStyles, customElement, html, css, observable, ref } from '@microsoft/fast-element';
import type { IMediaItem } from '../../../engine/providers/MediaPlugin';
import { Priority } from '../../../engine/taskpool/DeferredTask';

const spinner = 'data:image/svg+xml;base64,';

const styles: ElementStyles = css`

    :host {
        display: inline-block;
    }

    .preview {
        width: 320px;
        height: 320px;
        margin: calc(var(--base-height-multiplier) * 1px);
        display: flex;
        flex-direction: column;
        text-align: center;
    }

    .preview .info {
        flex: 0;
        padding: calc(var(--design-unit) * 1px);
        background-color: var(--neutral-layer-2);
        color: var(--neutral-foreground-hint);
    }

    .preview img {
        flex: 1;
        width: 100%;
        height: 100%;
        object-fit: contain;
    }
`;

const template: ViewTemplate<MediaItemPage> = html`
    <fluent-card class="preview">
        <div class="info">${model => model.info}</div>
        <img src="${model => model.img}" />
    </fluent-card>
`;

@customElement({ name: 'fluent-media-item-page', template, styles })
export class MediaItemPage extends FASTElement {

    override connectedCallback(): void {
        super.connectedCallback();
        this.Cleanup();
    }

    override disconnectedCallback(): void {
        super.disconnectedCallback();
        this.Cleanup();
    }

    private cancellator: AbortController;
    @observable img: string = spinner;
    @observable item: IMediaItem;
    itemChanged() {
        this.LoadPage();
    }
    @observable info = '┄';

    private async LoadPage() {
        this.Cleanup();
        if(!this.item) {
            return;
        }
        this.cancellator = new AbortController();
        try {
            const data = await this.item.Fetch(Priority.High, this.cancellator.signal);
            this.img = URL.createObjectURL(data);
            this.info = `${data.type} - ${data.size}`;
        } finally {
            this.cancellator = null;
        }
    }

    private Cleanup() {
        if(this.cancellator) {
            this.cancellator.abort();
            this.cancellator = null;
        }
        if(this.img && this.img.startsWith('blob:')) {
            URL.revokeObjectURL(this.img);
        }
        this.img = spinner;
        this.info = '┄';
    }
}